import React, { useState, useRef, useEffect } from 'react'
import Pixel from './Pixel'
import './App.sass'

export default function App() {
  const [width, setWidth] = useState(8)
  const [height, setHeight] = useState(8)
  const [color, setColor] = useState('#000000')
  const [erase, setErase] = useState(false)
  const [isDrawing, setIsDrawing] = useState(false)
  const containerRef = useRef(null)

  // start with sensible defaults

  function createGrid() {
    const container = containerRef.current
    if (!width || !height) {
      alert('Please pick a grid width and height greater than 0')
      return
    }
    if (width * height > 2000) {
      if (!confirm('Grid is large and may be slow. Continue?')) return
    }
    container.innerHTML = ''
    let count = 0
    for (let i = 0; i < height; i++) {
      count += 2
      const row = document.createElement('div')
      row.className = 'gridRow'
      for (let j = 0; j < width; j++) {
        count += 2
        const el = document.createElement('div')
        el.className = 'gridCol'
        el.id = `gridCol${count}`
        row.appendChild(el)
      }
      container.appendChild(row)
    }
  }

  function clearGrid() {
    const container = containerRef.current
    container.innerHTML = ''
  }

  useEffect(() => {
    const container = containerRef.current
    if (!container) return

    function pointerDown(e) {
      setIsDrawing(true)
      if (e.target.classList.contains('gridCol')) {
        e.target.style.backgroundColor = erase ? 'transparent' : color
      }
    }

    function pointerMove(e) {
      if (!isDrawing) return
      const x = e.clientX ?? (e.touches && e.touches[0].clientX)
      const y = e.clientY ?? (e.touches && e.touches[0].clientY)
      const el = document.elementFromPoint(x, y)
      if (el && el.classList.contains('gridCol')) {
        el.style.backgroundColor = erase ? 'transparent' : color
      }
    }

    function pointerUp() {
      setIsDrawing(false)
    }

    container.addEventListener('pointerdown', pointerDown)
    window.addEventListener('pointermove', pointerMove)
    window.addEventListener('pointerup', pointerUp)

    return () => {
      container.removeEventListener('pointerdown', pointerDown)
      window.removeEventListener('pointermove', pointerMove)
      window.removeEventListener('pointerup', pointerUp)
    }
  }, [isDrawing, color, erase])

  return (
    <div className="wrapper">
      <div className="options">
        <div className="opt-wrapper">
          <div className="slider">
            <label htmlFor="width-range">Grid Width</label>
            <input
              id="width-range"
              type="range"
              min="1"
              max="35"
              value={width}
              onChange={(e) => setWidth(Number(e.target.value))}
            />
            <span id="width-value">{width < 10 ? `0${width}` : width}</span>
          </div>
          <div className="slider">
            <label htmlFor="height-range">Grid Height</label>
            <input
              id="height-range"
              type="range"
              min="1"
              max="35"
              value={height}
              onChange={(e) => setHeight(Number(e.target.value))}
            />
            <span id="height-value">{height < 10 ? `0${height}` : height}</span>
          </div>
        </div>

        <div className="opt-wrapper">
          <button id="submit-grid" onClick={createGrid}>
            Create Grid
          </button>
          <button id="clear-grid" onClick={clearGrid}>
            Clear Grid
          </button>
          <input
            type="color"
            id="color-input"
            value={color}
            onChange={(e) => setColor(e.target.value)}
          />
          <button id="erase-btn" onClick={() => setErase(true)}>
            Erase
          </button>
          <button id="paint-btn" onClick={() => setErase(false)}>
            Paint
          </button>
        </div>
      </div>

      <div className="container" ref={containerRef}></div>
    </div>
  )
}
